mapper.update:
UPDATE scan
end_time = #{end_time}, status = #{status}, result_summary = #{result_summary},
where uuid = #{uuid}

mapper.select:
select * from scan

    repo_id=#{repo_id}
    
        and category = #{category}
    
    
        and commit_id = #{commit_id}
    

order by commit_time desc

select * from scan

    repo_id=#{repo_id}
    
        and status=#{status}
    

order by commit_time desc
