mapper.insert:
insert into tagged
      (
          item_id,
          tag_id
      )
values
      (
          #{item_id},
          #{tag_id}
      )

insert into tagged
      (
          item_id,
          tag_id
      )
values

    (
        #{item.item_id},
        #{item.tag_id}
    )

mapper.delete:
delete from tagged
where item_id in

    #{item_id}

mapper.select:
select distinct(item_id) from tagged
where tag_id in
#{tag_id}
